import React from 'react';
import Register from './components/Register';
import Login from './components/Login';
import axios from 'axios';

function App() {
  const handleProtectedRoute = async () => {
    const token = localStorage.getItem('jwtToken');

    if (!token) {
      alert('You must log in first.');
      return;
    }

    try {
      const response = await axios.get('http://localhost:5000/api/user/protected', {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert(`Protected route response: ${response.data.message}`);
    } catch (error) {
      if (error.response && error.response.data.error) {
        alert(`Error: ${error.response.data.error}`);
      } else {
        alert('Server error or unauthorized.');
      }
    }
  };

  return (
    <div className="bg-gray-100 min-h-screen flex flex-col justify-center items-center">
      <h1 className="text-3xl font-bold mb-8">Customer Portal</h1>
      <Register />
      <Login />
      <button
        className="mt-6 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        onClick={handleProtectedRoute}
      >
        Access Protected Route
      </button>
    </div>
  );
}

export default App;
