import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowRight, Globe, Shield, Clock, Menu, X } from 'lucide-react';
import { useSpring, animated } from 'react-spring';
import api from '../api';

const HomePage = () => {
  const [userData, setUserData] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeFeature, setActiveFeature] = useState(0);
  const navigate = useNavigate();

  // Features to display in an animated carousel
  const features = [
    {
      icon: <Globe className="w-12 h-12 text-indigo-600" />,
      title: "Global Reach",
      description: "Send payments to over 180 countries worldwide with real-time tracking and competitive rates."
    },
    {
      icon: <Shield className="w-12 h-12 text-indigo-600" />,
      title: "Bank-Grade Security",
      description: "Enterprise-level encryption and multi-factor authentication to keep your transactions secure."
    },
    {
      icon: <Clock className="w-12 h-12 text-indigo-600" />,
      title: "24/7 Processing",
      description: "Process payments around the clock with our automated SWIFT system."
    }
  ];

  // Animate features on load and interval change
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveFeature((prev) => (prev + 1) % features.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  // Check if the user is logged in
  useEffect(() => {
    const token = localStorage.getItem('jwtToken');
    if (token) {
      setIsLoggedIn(true);
      api.get('/api/user/profile')
        .then((response) => setUserData(response.data))
        .catch((error) => {
          console.error('Error:', error);
          setIsLoggedIn(false);
          localStorage.removeItem('jwtToken');
          navigate('/login');
        })
        .finally(() => setLoading(false));
    } else {
      setLoading(false);
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('jwtToken');
    setIsLoggedIn(false);
    setUserData(null);
    navigate('/login');
  };

  // Fade and transform animations
  const fadeInProps = useSpring({ opacity: 1, from: { opacity: 0 } });
  const bounceProps = useSpring({ transform: 'scale(1)', from: { transform: 'scale(0.8)' } });

  return (
    <div className="flex flex-col min-h-screen bg-gradient-to-b from-gray-100 to-gray-200 overflow-hidden">
      {/* Navbar */}


      {/* Hero Section */}
      <header className="relative bg-indigo-600 overflow-hidden">
        <div className="relative max-w-7xl mx-auto py-24 px-4 sm:px-6 lg:px-8 text-center">
          <animated.div style={bounceProps}>
            <h1 className="text-5xl sm:text-6xl md:text-7xl font-extrabold text-white mb-8 animate-fade-in">
              International Payment System
            </h1>
          </animated.div>
          <p className="text-2xl text-indigo-100 mb-12 animate-slide-up">
            Secure Transactions. Simplified Payments. Global Reach.
          </p>
          <button
            onClick={() => navigate('/login')}
            className="inline-flex items-center px-8 py-3 border border-transparent text-base font-medium rounded-md text-indigo-600 bg-white hover:bg-indigo-50 transition-all duration-300 transform hover:scale-110 shadow-lg"
          >
            Get Started <ArrowRight className="ml-2" />
          </button>
        </div>
      </header>

      {/* Features Section */}
      <section className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h2 className="text-3xl font-bold text-gray-900 mb-8">
            Powerful Features for Global Payments
          </h2>
          <p className="text-lg text-gray-600 mb-8 max-w-2xl mx-auto">
            Our platform combines cutting-edge technology with user-friendly design to make international payments seamless and secure.
          </p>
          <div className="grid md:grid-cols-3 gap-8">
            {features.map((feature, index) => (
              <div
                key={index}
                className={`p-6 bg-white rounded-xl shadow-lg transform transition-transform duration-500 hover:scale-110 ${
                  activeFeature === index ? 'border-2 border-indigo-500' : ''
                }`}
              >
                <animated.div style={bounceProps} className="flex justify-center mb-4 animate-bounce-slow">{feature.icon}</animated.div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">{feature.title}</h3>
                <p className="text-gray-600">{feature.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Team Section */}
      <section className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h2 className="text-4xl font-bold text-gray-900 mb-12">
            Meet Our Team
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
            {[
              {
                name: "Mpho Ndlela",
                role: "Team Lead",
                image: "https://media.licdn.com/dms/image/D4D03AQHYSGtq8VOslQ/profile-displayphoto-shrink_200_200/0/1710094803991",
                description: "Experienced in leading development teams for secure financial applications."
              },
              {
                name: "Alec Cowan",
                role: "Developer",
                image: "https://media.licdn.com/dms/image/D4E03AQGxJrhjkjg1JA/profile-displayphoto-shrink_200_200/0/1720016768535",
                description: "Specialist in building scalable, secure full-stack systems for financial institutions."
              },
              {
                name: "Lucian Young",
                role: "UI Designer",
                image: "https://media.licdn.com/dms/image/D5603AQHhjtgvN6VKHQ/profile-displayphoto-shrink_200_200/0/1713450059841",
                description: "Expert in designing intuitive and user-friendly financial interfaces."
              },
              {
                name: "Anele Siwela",
                role: "Back-End Developer",
                image: "https://media.licdn.com/dms/image/D4D03AQHI_NoiU-VqJw/profile-displayphoto-shrink_400_400/0/1705055843973",
                description: "Focused on optimizing backend systems and ensuring secure database transactions."
              }
            ].map((member, index) => (
              <div key={index} className="bg-white p-6 rounded-xl shadow-lg text-center transform transition-transform duration-500 hover:scale-110">
                <img
                  src={member.image}
                  alt={member.name}
                  className="w-32 h-32 rounded-full mx-auto mb-4 animate-bounce-slow"
                />
                <h3 className="text-xl font-semibold text-gray-900">{member.name}</h3>
                <p className="text-indigo-600">{member.role}</p>
                <p className="text-gray-600 mt-2">{member.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-indigo-900 text-white py-6 shadow-lg">
        <div className="text-center">
          <p className="text-lg mb-2">Risk Rangers Team</p>
          <p className="text-sm text-indigo-200">Mpho Ndlela • Alec Cowan • Lucian Young • Anele Siwela</p>
        </div>
      </footer>
    </div>
  );
};

export default HomePage;
