import React, { useState } from 'react';
import { Lock, User, CreditCard } from 'lucide-react';
import api from '../api';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    idNumber: '',
    username: '',
    accountNumber: '',
    password: '',
    confirmPassword: '',
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [submitStatus, setSubmitStatus] = useState({ type: '', message: '' });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (errors[name]) setErrors(prev => ({ ...prev, [name]: '' }));
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.name.trim()) newErrors.name = 'Name is required';
    if (!formData.idNumber.trim()) newErrors.idNumber = 'ID Number is required';
    if (!formData.username.trim()) newErrors.username = 'Username is required';
    if (!formData.accountNumber.trim()) newErrors.accountNumber = 'Account number is required';
    if (formData.password.length < 6) newErrors.password = 'Password must be at least 6 characters';
    if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = 'Passwords do not match';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setIsLoading(true);
    setSubmitStatus({ type: '', message: '' });

    try {
      const response = await api.post('/api/user/register', formData);

      if (response.status === 201) {
        setSubmitStatus({
          type: 'success',
          message: 'Registration successful! You can now log in.'
        });
        setTimeout(() => navigate('/login'), 2000);
      }
    } catch (error) {
      setSubmitStatus({
        type: 'error',
        message: error.response?.data?.details || 'Error registering user. Please try again.',
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-500 to-blue-600 p-6">
      <div className="w-full max-w-md bg-white p-8 rounded-lg shadow-2xl">
        <h2 className="text-4xl font-bold mb-6 text-center text-gray-800">Create Account</h2>

        {submitStatus.message && (
          <div className={`p-4 mb-4 rounded-md text-center font-medium ${submitStatus.type === 'success' ? 'bg-green-50 text-green-700' : 'bg-red-50 text-red-700'}`}>
            {submitStatus.message}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-6">
          {Object.keys(formData).map((field, index) => (
            <div key={index} className="relative">
              <User className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
              <input
                id={field}
                name={field}
                type={field.includes("password") ? "password" : "text"}
                placeholder={field.charAt(0).toUpperCase() + field.slice(1).replace(/([A-Z])/g, ' $1')}
                value={formData[field]}
                onChange={handleChange}
                className="w-full pl-10 pr-4 py-3 rounded-lg border shadow-sm focus:outline-none focus:border-indigo-400"
                required
              />
              {errors[field] && <span className="text-sm text-red-600">{errors[field]}</span>}
            </div>
          ))}

          <button
            type="submit"
            disabled={isLoading}
            className={`w-full py-3 rounded-lg text-lg font-semibold text-white transition-transform ${
              isLoading ? 'bg-indigo-300' : 'bg-gradient-to-r from-purple-500 to-blue-600 hover:shadow-lg hover:scale-105'
            }`}
          >
            {isLoading ? 'Registering...' : 'Register'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default Register;
