import { Trash2 } from 'lucide-react'; // Import the bin icon

const Transactions = () => {
  const [userProfile, setUserProfile] = useState(null);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);
  const [transactionToDelete, setTransactionToDelete] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('jwtToken');
    if (!token) {
      navigate('/login');
      return;
    }

    // Fetch user profile
    axios.get('https://localhost:5000/api/user/profile', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then((response) => {
      setUserProfile(response.data);
    })
    .catch(() => navigate('/login'));

    // Fetch user payments
    axios.get('https://localhost:5000/api/user/payments', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then((response) => setTransactions(response.data))
    .catch(() => setError('Error fetching payments.'))
    .finally(() => setLoading(false));
  }, [navigate]);

  const handleDelete = (transaction) => {
    setTransactionToDelete(transaction);
    setShowDeleteConfirmation(true); // Show confirmation popup
  };

  const confirmDelete = () => {
    const token = localStorage.getItem('jwtToken');
    axios.delete(`https://localhost:5000/api/user/payments/${transactionToDelete._id}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(() => {
      setTransactions(transactions.filter(t => t._id !== transactionToDelete._id));
    })
    .catch(() => setError('Error deleting transaction.'))
    .finally(() => {
      setShowDeleteConfirmation(false);
      setTransactionToDelete(null);
    });
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p className="text-red-600">{error}</p>;

  return (
    <div className="max-w-4xl mx-auto p-4 space-y-6">
      {/* Confirmation Popup */}
      {showDeleteConfirmation && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-4 rounded-lg shadow-lg">
            <p>Are you sure you want to cancel this transaction?</p>
            <div className="flex justify-end space-x-4 mt-4">
              <button onClick={confirmDelete} className="bg-red-600 text-white px-4 py-2 rounded-md">Yes, Delete</button>
              <button onClick={() => setShowDeleteConfirmation(false)} className="bg-gray-300 px-4 py-2 rounded-md">Cancel</button>
            </div>
          </div>
        </div>
      )}

      <div className="bg-white shadow-md rounded-lg p-6">
        <div className="border-b pb-4 mb-4 flex items-center">
          <Clock className="mr-2" />
          <h2 className="text-xl font-semibold">Transaction History</h2>
        </div>

        {transactions.length > 0 ? (
          <div className="overflow-y-auto h-[400px] pr-4">
            {transactions.map(transaction => (
              <div key={transaction._id} className="flex flex-col py-4 border-b last:border-0 relative">
                <div>
                  <p className="font-medium"><strong>Beneficiary:</strong> {transaction.beneficiaryName}</p>
                  <p className="text-sm text-gray-500"><strong>Reference:</strong> {transaction.paymentReference}</p>
                  <p className="text-sm text-gray-500"><strong>Date:</strong> {new Date(transaction.date).toLocaleDateString()}</p>
                  <p className={`text-sm font-bold ${transaction.status === 'denied' ? 'text-red-600' : transaction.status === 'approved' ? 'text-green-600' : 'text-gray-500'}`}>
                    <strong>Status:</strong> {transaction.status.charAt(0).toUpperCase() + transaction.status.slice(1)}
                  </p>
                </div>
                <div className="text-right mt-2">
                  <span className="font-semibold text-red-600">
                    -{transaction.currency} {Math.abs(transaction.amount).toFixed(2)}
                  </span>
                </div>

                {/* Delete Icon, only enabled for pending transactions */}
                {transaction.status === 'pending' && (
                  <button
                    onClick={() => handleDelete(transaction)}
                    className="absolute top-0 right-0 mt-2 mr-2 text-gray-500 hover:text-red-600 transition-colors"
                  >
                    <Trash2 className="h-5 w-5" />
                  </button>
                )}
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-500 text-center">No payments have been made yet.</p>
        )}
      </div>
    </div>
  );
};

export default Transactions;
