import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './components/Home'; // Import the new Home component
import Register from './components/Register';
import Login from './components/Login';
import axios from 'axios';

function App() {
  const handleProtectedRoute = async () => {
    const token = localStorage.getItem('jwtToken');

    if (!token) {
      alert('You must log in first.');
      return;
    }

    try {
      const response = await axios.get('http://localhost:5000/api/user/protected', {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert(`Protected route response: ${response.data.message}`);
    } catch (error) {
      if (error.response && error.response.data.error) {
        alert(`Error: ${error.response.data.error}`);
      } else {
        alert('Server error or unauthorized.');
      }
    }
  };

  return (
    <Router>
      <Navbar />
      <div className="pt-16"> {/* Padding to prevent overlap with fixed navbar */ }
        <Routes>
          <Route path="/" element={<Home />} /> {/* Home page */}
          <Route path="/login" element={<Login />} /> {/* Login page */}
          <Route path="/register" element={<Register />} /> {/* Register page */}
        </Routes>
      </div>

      {/* Only show the protected route button if the user is authenticated */}
      {localStorage.getItem('jwtToken') && (
        <div className="text-center mt-8">
          <button
            className="mt-6 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
            onClick={handleProtectedRoute}
          >
            Access Protected Route
          </button>
        </div>
      )}
    </Router>
  );
}

export default App;
