import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  ArrowRight,
  Globe,
  Shield,
  Clock,
  Sparkles,
  MessageCircle,
  Send,
} from 'lucide-react';
import { motion, useTransform, useSpring, useViewportScroll } from 'framer-motion';

const HomePage = () => {
  const navigate = useNavigate();
  const [activeFeature, setActiveFeature] = useState(0);
  const [mousePosition, setMousePosition] = useState({ x: -100, y: -100 });
  const [testimonials, setTestimonials] = useState(0);
  const { scrollY } = useViewportScroll();

  const features = [
    {
      icon: <Globe className="w-16 h-16 text-indigo-400" />,
      title: 'Global Reach',
      description: 'Send payments to over 180 countries worldwide with real-time tracking and competitive rates.',
    },
    {
      icon: <Shield className="w-16 h-16 text-indigo-400" />,
      title: 'Bank-Grade Security',
      description: 'Enterprise-level encryption and multi-factor authentication to keep your transactions secure.',
    },
    {
      icon: <Clock className="w-16 h-16 text-indigo-400" />,
      title: '24/7 Processing',
      description: 'Process payments around the clock with our automated SWIFT system.',
    },
    {
      icon: <Sparkles className="w-16 h-16 text-indigo-400" />,
      title: 'Innovative Tools',
      description: 'Leverage AI-powered analytics and forecasting tools to optimize your financial operations.',
    },
  ];

  const testimonialsData = [
    {
      quote: "This platform has revolutionized the way we handle international transactions. It's fast, secure, and incredibly user-friendly.",
      name: 'Jane Doe',
      company: 'Global Corp',
      image: 'https://example.com/jane.jpg',
    },
    {
      quote: 'Our international payments are now seamless and efficient thanks to this amazing service.',
      name: 'John Smith',
      company: 'Tech Innovations',
      image: 'https://example.com/john.jpg',
    },
    {
      quote: 'The real-time tracking feature gives us peace of mind with every transaction we make.',
      name: 'Maria Garcia',
