import React from 'react';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Register from './components/Register';
import Login from './components/Login';
import axios from 'axios';

function App() {
  const handleProtectedRoute = async () => {
    const token = localStorage.getItem('jwtToken');

    if (!token) {
      alert('You must log in first.');
      return;
    }

    try {
      const response = await axios.get('http://localhost:5000/api/user/protected', {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert(`Protected route response: ${response.data.message}`);
    } catch (error) {
      if (error.response && error.response.data.error) {
        alert(`Error: ${error.response.data.error}`);
      } else {
        alert('Server error or unauthorized.');
      }
    }
  };

  return (
    <div>
      <Navbar />
      <div className="container mt-5">
        <h1 className="text-center mb-5">Customer Portal</h1>
        <div className="row">
          <div className="col-md-6">
            <Register />
          </div>
          <div className="col-md-6">
            <Login />
          </div>
        </div>
        <div className="text-center mt-5">
          <button className="btn btn-primary" onClick={handleProtectedRoute}>
            Access Protected Route
          </button>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
