import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ArrowRight, Globe, Shield, Clock, Menu, X } from 'lucide-react';
import api from '../api';

const HomePage = () => {
  const [userData, setUserData] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeFeature, setActiveFeature] = useState(0);
  const navigate = useNavigate();

  const features = [
    {
      icon: <Globe className="w-12 h-12 text-indigo-600" />,
      title: "Global Reach",
      description: "Send payments to over 180 countries worldwide with real-time tracking and competitive rates."
    },
    {
      icon: <Shield className="w-12 h-12 text-indigo-600" />,
      title: "Bank-Grade Security",
      description: "Enterprise-level encryption and multi-factor authentication to keep your transactions secure."
    },
    {
      icon: <Clock className="w-12 h-12 text-indigo-600" />,
      title: "24/7 Processing",
      description: "Process payments around the clock with our automated SWIFT system."
    }
  ];

  useEffect(() => {
    const token = localStorage.getItem('jwtToken');
    if (token) {
      setIsLoggedIn(true);
      api.get('/api/user/profile')
        .then((response) => setUserData(response.data))
        .catch((error) => {
          console.error('Error:', error);
          setIsLoggedIn(false);
          localStorage.removeItem('jwtToken');
          navigate('/login');
        })
        .finally(() => setLoading(false));
    } else {
      setLoading(false);
    }
  }, [navigate]);

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveFeature((prev) => (prev + 1) % features.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('jwtToken');
    setIsLoggedIn(false);
    setUserData(null);
    navigate('/login');
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      {/* Navbar */}
      <nav className="bg-white shadow-lg">
        <div className="container mx-auto px-4">
          <div className="flex justify-between items-center py-4">
            <div className="text-2xl font-bold text-indigo-600">PaySystem</div>
            <button onClick={() => setIsMenuOpen(!isMenuOpen)} className="md:hidden">
              {isMenuOpen ? <X /> : <Menu />}
            </button>
            <div className="hidden md:flex items-center space-x-6">
              {isLoggedIn ? (
                <>
                  <button className="text-gray-600 hover:text-indigo-600 transition-colors">Dashboard</button>
                  <button onClick={handleLogout} className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors">
                    Logout
                  </button>
                </>
              ) : (
                <button onClick={() => navigate('/login')} className="px-4 py-2 bg-indigo-600 text-white rounded-lg hover:bg-indigo-700 transition-colors">
                  Login
                </button>
              )}
            </div>
          </div>
          {isMenuOpen && (
            <div className="md:hidden py-4">
              {isLoggedIn ? (
                <>
                  <button className="block w-full text-left py-2 text-gray-600 hover:text-indigo-600">Dashboard</button>
                  <button onClick={handleLogout} className="block w-full text-left py-2 text-gray-600 hover:text-indigo-600">
                    Logout
                  </button>
                </>
              ) : (
                <button onClick={() => navigate('/login')} className="block w-full text-left py-2 text-gray-600 hover:text-indigo-600">
                  Login
                </button>
              )}
            </div>
          )}
        </div>
      </nav>

      {/* Hero Section */}
      <header className="relative bg-indigo-600 overflow-hidden">
        <div className="relative max-w-7xl mx-auto py-24 px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h1 className="text-4xl sm:text-5xl md:text-6xl font-extrabold text-white mb-8">
              International Payment System
            </h1>
            <p className="text-xl md:text-2xl text-indigo-100 mb-12">
              Secure Transactions. Simplified Payments. Global Reach.
            </p>
            <button
              onClick={() => navigate('/login')}
              className="inline-flex items-center px-8 py-3 border border-transparent text-base font-medium rounded-md text-indigo-600 bg-white hover:bg-indigo-50 transition-colors duration-300"
            >
              Get Started <ArrowRight className="ml-2" />
            </button>
          </div>
        </div>
      </header>

      {/* Features Section */}
      <section className="py-20 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">
              Powerful Features for Global Payments
            </h2>
            <p className="text-lg text-gray-600 max-w-2xl mx-auto">
              Our platform combines cutting-edge technology with user-friendly design to make international payments seamless and secure.
            </p>
          </div>
          <div className="grid md:grid-cols-3 gap-8">
            {features.map((feature, index) => (
              <div
                key={index}
                className={`p-6 bg-white rounded-xl shadow-md transform transition-all duration-300 hover:scale-105 ${
                  activeFeature === index ? 'border-2 border-indigo-500' : ''
                }`}
              >
                <div className="flex justify-center mb-4">{feature.icon}</div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2 text-center">{feature.title}</h3>
                <p className="text-gray-600 text-center">{feature.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Team Section */}
      <section className="py-20 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <h2 className="text-3xl font-bold text-gray-900 mb-12 text-center">Meet Our Team</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
            {[
              {
                name: "Mpho Ndlela",
                role: "Team Lead",
                image: "https://media.licdn.com/dms/image/D4D03AQHYSGtq8VOslQ/profile-displayphoto-shrink_200_200/0/1710094803991",
                description: "Experienced in leading development teams for secure financial applications."
              },
              {
                name: "Alec Cowan",
                role: "Developer",
                image: "https://media.licdn.com/dms/image/D4E03AQGxJrhjkjg1JA/profile-displayphoto-shrink_200_200/0/1720016768535",
                description: "Specialist in building scalable, secure full-stack systems for financial institutions."
              },
              {
                name: "Lucian Young",
                role: "UI Designer",
                image: "https://media.licdn.com/dms/image/D5603AQHhjtgvN6VKHQ/profile-displayphoto-shrink_200_200/0/1713450059841",
                description: "Expert in designing intuitive and user-friendly financial interfaces."
              },
              {
                name: "Anele Siwela",
                role: "Back-End Developer",
                image: "https://media.licdn.com/dms/image/v2/D4D03AQHI_NoiU-VqJw/profile-displayphoto-shrink_800_800/profile-displayphoto-shrink_800_800/0/1705055843973?e=1736380800&v=beta&t=8lqOyau2hXYQC0y62lmunstc0hPhbITsyPlG3PnZZRk",
                description: "Focused on optimizing backend systems and ensuring secure database transactions."
              }
            ].map((member, index) => (
