import React, { useState, useContext, useEffect, useRef } from 'react';
import { Menu, X, User, Search, Bell, Settings, LogOut } from 'lucide-react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { 
  Alert,
  AlertDescription 
} from "@/components/ui/alert";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

const Navbar = () => {
  // State management
  const [isOpen, setIsOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [showNotification, setShowNotification] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  // Refs and context
  const searchInputRef = useRef(null);
  const { isLoggedIn, userRole, logout, user } = useContext(AuthContext);

  // Hooks
  const navigate = useNavigate();
  const location = useLocation();

  // Handle scroll effects
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 10);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Close mobile menu on route change
  useEffect(() => {
    setIsOpen(false);
  }, [location.pathname]);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyPress = (e) => {
      // Command/Ctrl + K to focus search
      if ((e.metaKey || e.ctrlKey) && e.key === 'k') {
        e.preventDefault();
        searchInputRef.current?.focus();
      }
      // Escape to close mobile menu
      if (e.key === 'Escape' && isOpen) {
        setIsOpen(false);
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [isOpen]);

  const handleSignOut = async () => {
    try {
      await logout();
      setShowNotification(true);
      setTimeout(() => {
        setShowNotification(false);
        navigate('/', { replace: true });
      }, 1500);
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/search?q=${encodeURIComponent(searchQuery)}`);
    }
  };

  const menuItems = [
    { label: 'Home', href: '/', icon: null },
    !isLoggedIn && { label: 'Login', href: '/login', icon: null },
    !isLoggedIn && { label: 'Register', href: '/register', icon: null },
    isLoggedIn && userRole === 'user' && { 
      label: 'Transactions', 
      href: '/transactions',
      icon: null 
    },
    isLoggedIn && userRole === 'admin' && { 
      label: 'Administrators',
      href: '/admin',
      icon: null
    },
    isLoggedIn && userRole === 'admin' && { 
      label: 'Pending Transactions',
      href: '/pending-transactions',
      icon: null
    }
  ].filter(Boolean);

  return (
    <>
      <nav 
        className={`fixed w-full z-50 transition-all duration-200 ${
          isScrolled 
            ? 'bg-white/80 backdrop-blur-md shadow-sm' 
            : 'bg-white'
        }`}
        role="navigation"
        aria-label="Main navigation"
      >
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            {/* Logo and Brand */}
            <div className="flex items-center">
              <Link 
                to="/" 
                className="flex items-center"
                aria-label="Home"
              >
                <div className="h-8 w-8 bg-indigo-600 rounded-lg flex items-center justify-center transition-transform hover:scale-105">
                  <span className="text-white font-bold text-xl">CP</span>
                </div>
                <span className="ml-2 text-xl font-semibold text-gray-900">
                  Customer Portal
                </span>
              </Link>

              {/* Desktop Navigation */}
              <div className="hidden sm:ml-6 sm:flex sm:space-x-8">
                {menuItems.map((item) => (
                  <Link
                    key={item.label}
                    to={item.href}
                    className={`border-transparent hover:border-gray-300 hover:text-gray-700 inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium transition-colors ${
                      location.pathname === item.href
                        ? 'border-indigo-500 text-gray-900'
                        : 'text-gray-500'
                    }`}
                  >
                    {item.label}
                  </Link>
                ))}
              </div>
            </div>

            {/* Desktop Right Section */}
            <div className="hidden sm:ml-6 sm:flex sm:items-center space-x-4">
              {/* Search Bar */}
              <form onSubmit={handleSearch} className="relative">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Search className="h-5 w-5 text-gray-400" />
                </div>
                <input
                  ref={searchInputRef}
                  type="search"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  placeholder="Search... (⌘/Ctrl + K)"
                  className="w-64 pl-10 py-2 rounded-md border border-gray-300 focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500 transition-all"
                  aria-label="Search"
                />
              </form>

              {/* User Menu Dropdown */}
              {isLoggedIn && (
                <DropdownMenu>
                  <DropdownMenuTrigger className="flex items-center">
                    <div className="relative">
                      <User className="h-8 w-8 rounded-full bg-gray-100 p-1" />
                      <span className="absolute -bottom-1 -right-1 h-3 w-3 rounded-full bg-green-400 border-2 border-white" />
                    </div>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end" className="w-56">
                    <DropdownMenuLabel>
                      <div className="flex flex-col space-y-1">
                        <p className="text-sm font-medium leading-none">{user?.name}</p>
                        <p className="text-xs leading-none text-gray-500">{user?.email}</p>
                      </div>
                    </DropdownMenuLabel>
                    <DropdownMenuSeparator />
                    <DropdownMenuItem>
                      <Settings className="mr-2 h-4 w-4" />
                      <span>Settings</span>
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={handleSignOut}>
                      <LogOut className="mr-2 h-4 w-4" />
                      <span>Sign out</span>
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              )}
            </div>

            {/* Mobile Menu Button */}
            <div className="flex items-center sm:hidden">
              <button 
                onClick={() => setIsOpen(!isOpen)}
                className="p-2 rounded-md hover:bg-gray-100 transition-colors"
                aria-expanded={isOpen}
                aria-label="Toggle menu"
              >
                {isOpen ? (
                  <X className="h-6 w-6 text-gray-500" />
                ) : (
                  <Menu className="h-6 w-6 text-gray-500" />
                )}
              </button>
            </div>
          </div>
        </div>

        {/* Mobile Menu */}
        <div
          className={`sm:hidden transition-all duration-200 ease-in-out ${
            isOpen ? 'max-h-screen opacity-100' : 'max-h-0 opacity-0 overflow-hidden'
          }`}
        >
          <div className="pt-2 pb-3 space-y-1 px-4">
            {menuItems.map((item) => (
              <Link
                key={item.label}
                to={item.href}
                className={`block px-3 py-2 rounded-md text-base font-medium transition-colors ${
                  location.pathname === item.href
                    ? 'bg-indigo-50 text-indigo-700'
                    : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900'
                }`}
              >
                {item.label}
              </Link>
            ))}
            {isLoggedIn && (
              <button
                onClick={handleSignOut}
                className="w-full text-left px-3 py-2 rounded-md text-base font-medium text-gray-600 hover:bg-gray-50 hover:text-gray-900"
              >
                Sign out
              </button>
            )}
          </div>
        </div>
      </nav>

      {/* Notification Alert */}
      {showNotification && (
        <Alert className="fixed bottom-4 right-4 w-72 bg-green-50 border-green-200">
          <AlertDescription>
            Successfully signed out! Redirecting...
          </AlertDescription>
        </Alert>
      )}
    </>
  );
};

export default Navbar;
