// Transactions.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Transactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('jwtToken');

    if (!token) {
      alert('You must log in first.');
      navigate('/login'); // Redirect to login if token is missing
      return;
    }

    // Fetch user profile
    axios.get('https://localhost:5000/api/user/profile', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then((response) => {
      console.log('Profile Data:', response.data); // Log profile data
      return axios.get('https://localhost:5000/api/user/transactions', {
        headers: { Authorization: `Bearer ${token}` }
      });
    })
    .then((response) => {
      console.log('Fetched Transactions:', response.data); // Log transaction data
      setTransactions(response.data); // Set transactions to state
    })
    .catch((error) => {
      console.error('Error fetching user data or transactions:', error);
      if (error.response) {
        // If the error comes from the server
        setError(error.response.data.error || 'An unknown error occurred.');
      } else {
        setError('An error occurred while connecting to the server.');
      }
      navigate('/login'); // Redirect to login if there's an error
    })
    .finally(() => {
      setLoading(false); // End loading state
    });
  }, [navigate]);

  if (loading) return <p>Loading...</p>; // Show loading state

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      {error && <p className="text-red-600">{error}</p>} {/* Display error if any */}
      {transactions.length === 0 ? (
        <p>No transactions found.</p> // Message for no transactions
      ) : (
        <ul>
          {transactions.map((transaction) => (
            <li key={transaction._id} className="mb-4">
              <p><strong>Amount:</strong> ${transaction.amount}</p>
              <p><strong>Date:</strong> {new Date(transaction.date).toLocaleDateString()}</p>
              <p><strong>Description:</strong> {transaction.description}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Transactions;
