import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // For navigation

const Transactions = () => {
  const [userData, setUserData] = useState(null);
  const [transactions, setTransactions] = useState([]); // State for transactions
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('jwtToken');

    // Check if the user is authenticated
    if (!token) {
      alert('You must log in first.');
      navigate('/login'); // Redirect to the login page if not authenticated
      return;
    }

    // Fetch user data
    axios.get('https://localhost:5000/api/user/profile', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then((response) => {
      setUserData(response.data);
      // Fetch transactions for the user
      return axios.get('https://localhost:5000/api/user/transactions', {
        headers: { Authorization: `Bearer ${token}` }
      });
    })
    .then((response) => {
      setTransactions(response.data); // Set transactions state
    })
    .catch((error) => {
      console.error('Error fetching user data or transactions:', error);
      navigate('/login'); // Redirect to login if there's an error
    })
    .finally(() => {
      setLoading(false); // End loading state
    });
  }, [navigate]);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="container mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <h1 className="text-3xl font-bold mb-4 text-center">
        Welcome, {userData.username}!
      </h1>

      <h2 className="text-2xl font-bold mb-6 text-center">
        Your Transaction Log
      </h2>

      <div className="bg-white p-6 rounded-lg shadow-md">
        {transactions.length === 0 ? (
          <p>No transactions found.</p>
        ) : (
          <ul>
            {transactions.map((transaction) => (
              <li key={transaction._id} className="mb-4">
                <p><strong>Amount:</strong> ${transaction.amount}</p>
                <p><strong>Date:</strong> {new Date(transaction.date).toLocaleDateString()}</p>
                <p><strong>Description:</strong> {transaction.description}</p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Transactions;
