import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  ArrowRight,
  Globe,
  Shield,
  Clock,
  Sparkles,
  Users,
  MessageCircle,
  Send,
} from 'lucide-react';
import { motion, useViewportScroll, useTransform } from 'framer-motion';

const HomePage = () => {
  const navigate = useNavigate();
  const [activeFeature, setActiveFeature] = useState(0);
  const [mousePosition, setMousePosition] = useState({ x: -100, y: -100 });
  const [parallaxY, setParallaxY] = useState(0);
  const [testimonials, setTestimonials] = useState(0);
  const { scrollY } = useViewportScroll();
  const ref = useRef(null);

  const features = [
    {
      icon: <Globe className="w-16 h-16 text-indigo-400" />,
      title: 'Global Reach',
      description:
        'Send payments to over 180 countries worldwide with real-time tracking and competitive rates.',
    },
    {
      icon: <Shield className="w-16 h-16 text-indigo-400" />,
      title: 'Bank-Grade Security',
      description:
        'Enterprise-level encryption and multi-factor authentication to keep your transactions secure.',
    },
    {
      icon: <Clock className="w-16 h-16 text-indigo-400" />,
      title: '24/7 Processing',
      description:
        'Process payments around the clock with our automated SWIFT system.',
    },
    {
      icon: <Sparkles className="w-16 h-16 text-indigo-400" />,
      title: 'Innovative Tools',
      description:
        'Leverage AI-powered analytics and forecasting tools to optimize your financial operations.',
    },
  ];

  const testimonialsData = [
    {
      quote:
        "This platform has revolutionized the way we handle international transactions. It's fast, secure, and incredibly user-friendly.",
      name: 'Jane Doe',
      company: 'Global Corp',
      image: 'https://example.com/jane.jpg',
    },
    {
      quote:
        'Our international payments are now seamless and efficient thanks to this amazing service.',
      name: 'John Smith',
      company: 'Tech Innovations',
      image: 'https://example.com/john.jpg',
    },
    {
      quote:
        'The real-time tracking feature gives us peace of mind with every transaction we make.',
      name: 'Maria Garcia',
      company: 'Finance Experts',
      image: 'https://example.com/maria.jpg',
    },
  ];

  // Mouse move effect
  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  // Parallax effect
  useEffect(() => {
    const handleScroll = () => {
      setParallaxY(window.scrollY);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Rotate features
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveFeature((prev) => (prev + 1) % features.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [features.length]);

  // Rotate testimonials
  useEffect(() => {
    const interval = setInterval(() => {
      setTestimonials((prev) => (prev + 1) % testimonialsData.length);
    }, 8000);
    return () => clearInterval(interval);
  }, [testimonialsData.length]);

  // Framer Motion parallax
  const y1 = useTransform(scrollY, [0, 500], [0, -100]);
  const y2 = useTransform(scrollY, [0, 500], [0, 50]);

  return (
    <div className="flex flex-col min-h-screen overflow-hidden relative">
      {/* Interactive Cursor */}
      <div
        className="pointer-events-none fixed z-50 h-12 w-12 rounded-full bg-gradient-to-br from-purple-500 to-indigo-500 opacity-80 mix-blend-multiply"
        style={{
          left: `${mousePosition.x}px`,
          top: `${mousePosition.y}px`,
          transform: 'translate(-50%, -50%)',
        }}
      ></div>

      {/* Hero Section */}
      <header className="relative min-h-screen bg-gradient-to-br from-indigo-900 via-purple-800 to-indigo-900 overflow-hidden">
        {/* Animated Background */}
        <motion.div
          className="absolute inset-0"
          style={{ y: y1 }}
          ref={ref}
        >
          <div className="absolute inset-0 overflow-hidden">
            {[...Array(50)].map((_, i) => (
              <div
                key={i}
                className="absolute bg-white/5 rounded-full blur-3xl"
                style={{
                  width: Math.random() * 400 + 50,
                  height: Math.random() * 400 + 50,
                  left: `${Math.random() * 100}%`,
                  top: `${Math.random() * 100}%`,
                  opacity: 0.2,
                  animation: `float ${Math.random() * 10 + 5}s ease-in-out infinite`,
                }}
              />
            ))}
          </div>
        </motion.div>

        {/* Hero Content */}
        <div className="relative max-w-7xl mx-auto py-40 px-4 sm:px-6 lg:px-8 text-center">
          <motion.div
            className="space-y-8"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1 }}
          >
            <h1 className="text-7xl font-black text-white mb-8 animate-fade-in bg-clip-text text-transparent bg-gradient-to-r from-white via-purple-200 to-white">
              International Payment System
            </h1>
            <p className="text-3xl text-indigo-200 mb-12 font-light">
              Secure Transactions • Simplified Payments • Global Reach
            </p>
            <button
              onClick={() => navigate('/login')}
              className="group relative inline-flex items-center px-12 py-4 text-lg font-medium rounded-full text-white bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 hover:from-indigo-600 hover:via-purple-600 hover:to-pink-600 transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-2xl"
            >
              <span className="relative">Get Started</span>
              <ArrowRight className="ml-2 group-hover:translate-x-1 transition-transform" />
            </button>
          </motion.div>
        </div>
      </header>

      {/* Features Section */}
      <section className="py-32 bg-gradient-to-b from-indigo-900 to-purple-900">
        <motion.div
          className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <div className="text-center mb-20">
            <h2 className="text-4xl font-bold text-white mb-8 bg-clip-text text-transparent bg-gradient-to-r from-purple-200 to-pink-200">
              Powerful Features for Global Payments
            </h2>
            <p className="text-xl text-indigo-200 max-w-2xl mx-auto">
              Our platform combines cutting-edge technology with user-friendly design to make international payments seamless and secure.
            </p>
          </div>
          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
            {features.map((feature, index) => (
              <motion.div
                key={index}
                className={`p-8 rounded-2xl backdrop-blur-xl bg-white/10 border border-white/20 transform transition-all duration-500 hover:scale-105 ${
                  activeFeature === index ? 'ring-2 ring-purple-400 shadow-lg shadow-purple-500/20' : ''
                }`}
                whileHover={{ scale: 1.05 }}
              >
                <div className="flex flex-col items-center text-center space-y-4">
                  <div className="p-4 rounded-full bg-gradient-to-br from-indigo-500/20 to-purple-500/20">
                    {feature.icon}
                  </div>
                  <h3 className="text-2xl font-semibold text-white">{feature.title}</h3>
                  <p className="text-indigo-200">{feature.description}</p>
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </section>

      {/* Testimonials Section */}
      <section className="py-32 bg-gradient-to-b from-purple-900 to-indigo-900">
        <motion.div
          className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <h2 className="text-5xl font-bold text-center text-white mb-20 bg-clip-text text-transparent bg-gradient-to-r from-purple-200 to-pink-200">
            What Our Clients Say
          </h2>
          <div className="relative">
            <div className="overflow-hidden">
              <motion.div
                className="flex"
                animate={{ x: `-${testimonials * 100}%` }}
                transition={{ duration: 0.5 }}
              >
                {testimonialsData.map((testimonial, index) => (
                  <div key={index} className="flex-shrink-0 w-full">
                    <div className="bg-white/10 backdrop-blur-xl p-12 rounded-2xl border border-white/10">
                      <p className="text-xl text-indigo-200 mb-8">"{testimonial.quote}"</p>
                      <div className="flex items-center">
                        <img
                          src={testimonial.image}
                          alt={testimonial.name}
                          className="w-16 h-16 rounded-full object-cover mr-4"
                        />
                        <div>
                          <p className="text-white font-semibold">{testimonial.name}</p>
                          <p className="text-indigo-300">{testimonial.company}</p>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </motion.div>
            </div>
            <div className="flex justify-center mt-8 space-x-4">
              {testimonialsData.map((_, index) => (
                <button
                  key={index}
                  className={`h-3 w-3 rounded-full ${
                    testimonials === index ? 'bg-purple-500' : 'bg-white/20'
                  }`}
                  onClick={() => setTestimonials(index)}
                ></button>
              ))}
            </div>
          </div>
        </motion.div>
      </section>

      {/* Newsletter Section */}
      <section className="py-32 bg-gradient-to-b from-indigo-900 to-purple-900">
        <motion.div
          className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 text-center"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <h2 className="text-5xl font-bold text-white mb-8 bg-clip-text text-transparent bg-gradient-to-r from-purple-200 to-pink-200">
            Stay Updated
          </h2>
          <p className="text-xl text-indigo-200 mb-12">
            Subscribe to our newsletter to get the latest news and updates.
          </p>
          <div className="flex justify-center">
            <div className="relative w-full max-w-md">
              <input
                type="email"
                className="w-full px-6 py-4 rounded-full bg-white/10 text-white placeholder-indigo-300 focus:outline-none"
                placeholder="Enter your email"
              />
              <button className="absolute right-0 top-0 h-full px-6 rounded-full bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 hover:from-indigo-600 hover:via-purple-600 hover:to-pink-600 transition-all duration-300">
                <Send className="text-white" />
              </button>
            </div>
          </div>
        </motion.div>
      </section>

      {/* Team Section */}
      <section className="py-32 bg-gradient-to-b from-purple-900 to-indigo-900">
        <motion.div
          className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <h2 className="text-5xl font-bold text-center text-white mb-20 bg-clip-text text-transparent bg-gradient-to-r from-purple-200 to-pink-200">
            Meet Our Team
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-12">
            {[
              {
                name: 'Mpho Ndlela',
                role: 'Team Lead',
                image: 'https://example.com/image1.jpg',
                description:
                  'Experienced in leading development teams for secure financial applications.',
              },
              {
                name: 'Alec Cowan',
                role: 'Developer',
                image: 'https://example.com/image2.jpg',
                description:
                  'Specialist in building scalable, secure full-stack systems for financial institutions.',
              },
              {
                name: 'Lucian Young',
                role: 'UI Designer',
                image: 'https://example.com/image3.jpg',
                description:
                  'Expert in designing intuitive and user-friendly financial interfaces.',
              },
              {
                name: 'Anele Siwela',
                role: 'Back-End Developer',
                image: 'https://example.com/image4.jpg',
                description:
                  'Focused on optimizing backend systems and ensuring secure database transactions.',
              },
            ].map((member, index) => (
              <motion.div
                key={index}
                className="group relative overflow-hidden rounded-2xl bg-gradient-to-br from-indigo-500/10 to-purple-500/10 backdrop-blur-xl border border-white/10 p-6 transform transition-all duration-500 hover:scale-105"
                whileHover={{ scale: 1.05 }}
              >
                <div className="absolute inset-0 bg-gradient-to-br from-indigo-600/20 to-purple-600/20 opacity-0 group-hover:opacity-100 transition-opacity duration-500" />
                <div className="relative z-10">
                  <div className="relative w-48 h-48 mx-auto mb-6">
                    <div className="absolute inset-0 rounded-full bg-gradient-to-br from-indigo-500 to-purple-500 animate-pulse" />
                    <img
                      src={member.image}
                      alt={member.name}
                      className="relative w-full h-full rounded-full object-cover p-1"
                    />
                  </div>
                  <h3 className="text-2xl font-semibold text-white text-center mb-2">
                    {member.name}
                  </h3>
                  <p className="text-indigo-300 text-center mb-4">{member.role}</p>
                  <p className="text-indigo-200 text-center text-sm">{member.description}</p>
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </section>

      {/* Contact Section */}
      <section className="py-32 bg-gradient-to-b from-indigo-900 to-purple-900">
        <motion.div
          className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 1 }}
        >
          <h2 className="text-5xl font-bold text-center text-white mb-12 bg-clip-text text-transparent bg-gradient-to-r from-purple-200 to-pink-200">
            Get in Touch
          </h2>
          <div className="bg-white/10 backdrop-blur-xl p-12 rounded-2xl border border-white/10">
            <form className="grid grid-cols-1 md:grid-cols-2 gap-8">
              <div className="col-span-1">
                <input
                  type="text"
                  className="w-full px-6 py-4 rounded-full bg-white/10 text-white placeholder-indigo-300 focus:outline-none"
                  placeholder="Your Name"
                />
              </div>
              <div className="col-span-1">
                <input
                  type="email"
                  className="w-full px-6 py-4 rounded-full bg-white/10 text-white placeholder-indigo-300 focus:outline-none"
                  placeholder="Your Email"
                />
              </div>
              <div className="col-span-2">
                <textarea
                  className="w-full px-6 py-4 rounded-3xl bg-white/10 text-white placeholder-indigo-300 focus:outline-none"
                  placeholder="Your Message"
                  rows="5"
                ></textarea>
              </div>
              <div className="col-span-2 text-center">
                <button className="inline-flex items-center px-12 py-4 text-lg font-medium rounded-full text-white bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 hover:from-indigo-600 hover:via-purple-600 hover:to-pink-600 transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-2xl">
                  <span className="relative">Send Message</span>
                  <MessageCircle className="ml-2" />
                </button>
              </div>
            </form>
          </div>
        </motion.div>
      </section>

      {/* Enhanced Footer */}
      <footer className="bg-indigo-950 text-white py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center space-y-4">
            <h3 className="text-2xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-pink-400">
              Risk Rangers Team
            </h3>
            <p className="text-lg text-indigo-300">
              Mpho Ndlela • Alec Cowan • Lucian Young • Anele Siwela
            </p>
            <div className="flex justify-center space-x-6 mt-4">
              <a href="#" className="text-indigo-300 hover:text-white">
                Privacy Policy
              </a>
              <a href="#" className="text-indigo-300 hover:text-white">
                Terms of Service
              </a>
              <a href="#" className="text-indigo-300 hover:text-white">
                Contact Us
              </a>
            </div>
          </div>
        </div>
      </footer>

      {/* Scroll to Top Button */}
      <button
        className="fixed bottom-8 right-8 p-4 rounded-full bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 text-white shadow-lg hover:from-indigo-600 hover:via-purple-600 hover:to-pink-600 transition-all duration-300"
        onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
      >
        <ArrowRight className="transform rotate-90" />
      </button>
    </div>
  );
};

export default HomePage;
