import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Navigation hook

const Transactions = () => {
  const [userProfile, setUserProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('jwtToken');
    if (!token) {
      navigate('/login'); // Redirect to login if token is missing
      return;
    }

    // Fetch user profile
    axios.get('https://localhost:5000/api/user/profile', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then((response) => {
      setUserProfile(response.data); // Set user profile data to state
    })
    .catch((error) => {
      console.error('Error fetching user profile:', error);
      setError('Error fetching user profile.');
      navigate('/login'); // Redirect to login if there's an error
    })
    .finally(() => {
      setLoading(false); // End loading state
    });
  }, [navigate]);

  if (loading) return <p>Loading...</p>; // Show loading state
  if (error) return <p className="text-red-600">{error}</p>; // Show error

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      {userProfile ? (
        <div>
          <p><strong>Name:</strong> {userProfile.name}</p>
          <p><strong>Email:</strong> {userProfile.email}</p>
          <p><strong>Account Number:</strong> {userProfile.accountNumber}</p>
          {/* Add more personal information fields as needed */}
        </div>
      ) : (
        <p>No profile information available.</p>
      )}
    </div>
  );
};

export default Transactions;
