// AuthContext.js
import React, { createContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState('');

  // Check if the user is logged in when the app loads
  useEffect(() => {
    const token = localStorage.getItem('jwtToken');
    if (token) {
      try {
        const decodedToken = JSON.parse(atob(token.split('.')[1])); // Decode the JWT token
        setIsLoggedIn(true);
        setUserRole(decodedToken.role); // Set the user role (admin or user)
      } catch (error) {
        console.error('Error decoding token:', error);
        setIsLoggedIn(false); // If there's an error, treat it as logged out
        setUserRole(''); // Clear role if there is an issue
      }
    } else {
      setIsLoggedIn(false); // If no token, user is logged out
      setUserRole(''); // Clear role
    }
  }, []);

  const login = (token) => {
    localStorage.setItem('jwtToken', token);
    const decodedToken = JSON.parse(atob(token.split('.')[1])); // Decode the JWT token
    setIsLoggedIn(true);
    setUserRole(decodedToken.role);
  };

  const logout = () => {
    localStorage.removeItem('jwtToken');
    setIsLoggedIn(false);
    setUserRole('');
  };

  return (
    <AuthContext.Provider value={{ isLoggedIn, userRole, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
