import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom'; // To get the state passed via navigate
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function HomePage() {
  const [userData, setUserData] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const location = useLocation(); // Get location object

  useEffect(() => {
    const token = localStorage.getItem('jwtToken');
    
    if (token) {
      setIsLoggedIn(true);
      // Fetch user data from the server
      axios.get('https://localhost:5000/api/user/profile', {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then((response) => {
        setUserData(response.data);
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
      });
    }

    // Show the toast notification if login was successful
    if (location.state?.loginSuccess) {
      toast.success('You have logged in successfully!', {
        position: toast.POSITION.TOP_RIGHT,
      });
    }
  }, [location.state]);

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      {/* Header Section */}
      <header className="bg-indigo-600 py-8">
        <h1 className="text-4xl font-bold text-center text-white mb-4 animate-fade-in">
          International Payment System
        </h1>
        <p className="text-xl text-center text-white animate-slide-up">
          Secure Transactions. Simplified Payments.
        </p>
      </header>

      {/* Overview Section */}
      <main className="flex-1 py-8">
        <div className="container mx-auto px-4 sm:px-6 lg:px-8">
          <section className="mb-12 text-center">
            <h2 className="text-3xl font-bold mb-6">Our Payment Portal Features</h2>
            <p className="text-lg text-gray-600">
              Streamline your international payments with our secure SWIFT-based payment system. 
              Our platform ensures that your transactions are safe, efficient, and globally compliant.
            </p>
          </section>

          {/* Transaction Section (Visible only to logged-in users) */}
          {isLoggedIn && userData && (
            <section className="mb-12 text-center bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-3xl font-bold mb-6">Your Transaction Information</h2>
              <p className="text-lg text-gray-600 mb-4">
                Welcome back, <strong>{userData.username}</strong>! Here are your personal details:
              </p>
              <div className="text-left">
                <p><strong>Username:</strong> {userData.username}</p>
                <p><strong>Account Number:</strong> {userData.accountNumber}</p>
                <p><strong>ID Number:</strong> {userData.idNumber}</p>
              </div>
            </section>
          )}

          {/* Team Section */}
          <section className="mb-16">
            <h2 className="text-3xl font-bold text-gray-800 mb-8 text-center">Meet Our Team</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
              {/* Team cards */}
            </div>
          </section>
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-indigo-600 text-white p-4 text-center mt-auto">
        <p>Done by the Risk Rangers: Mpho Ndlela, Alec Cowan, Lucian Young, Anele Siwela</p>
      </footer>

      {/* Add ToastContainer here */}
      <ToastContainer />
    </div>
  );
}

export default HomePage;
